import osOPTIONS = {}"""Parameters for Learned Index Structure for Spatial Data (LISA)Args:    page_size:          Predefined page size"""OPTIONS['page_size'] = 0OPTIONS['num_partitions_mapped_values'] = 10OPTIONS['num_breakpoints'] = 10                # SigmaOPTIONS['est_avg_num_keys_shard'] = 10         # Psi# Machine learning trainingOPTIONS['max_iterations'] = 100OPTIONS['tolerance'] = 10**(-7)OPTIONS['learning_rates'] = [2, 1, 0.5, 0.4, 0.3, 0.2, 0.1, 0.01, 0.001, 0.0001]# Number of partitions. For simplicity, T_i = T_j, for all i, j in R^dOPTIONS['num_partitions'] = [3, 3]       #TODO: T_i < 300# Datasetimis_3months_dataset = {'name' : 'imis_3months',                        'data_folder' : os.path.join('.', 'data', 'imis_3months'),                        'file_name' : 'imis_3months.txt',                        'labels' : ('lon', 'lat')}imis_3months_dataset_subset1000 = {'name' : 'imis_3months_subset1000',                                   'data_folder' : os.path.join('.', 'data', 'imis_3months'),                                   'file_name' : 'imis_3months_subset1000.txt',                                   'labels' : ('lon', 'lat')}imis_3months_dataset_subset10000 = {'name' : 'imis_3months_subset10000',                                    'data_folder' : os.path.join('.', 'data', 'imis_3months'),                                    'file_name' : 'imis_3months_subset10000.txt',                                    'labels' : ('lon', 'lat')}OPTIONS['datasets'] = imis_3months_dataset_subset1000