import osOPTIONS = {}"""Parameters for Learned Index Structure for Spatial Data (LISA)Args:    num_partitions:         Number of partitions along each dimension"""# Index ParametersOPTIONS['num_partitions'] = [10, 10]            # Number of partitions. For simplicity, T_i = T_j, for all i, j in R^d                                                # T_i = 240 for the paperOPTIONS['num_partitions_mapped_values'] = 50OPTIONS['num_breakpoints'] = 100                # Sigma (paper did 100); number of breakpoints in SP's each piecewise linear functionOPTIONS['est_avg_num_keys_shard'] = 100         # Psi   (did omega)OPTIONS['max_num_keys_per_page'] = 100          # Omega (paper did PS / (MS + AS) ~=113)# Machine learning trainingOPTIONS['max_iterations'] = 100OPTIONS['tolerance'] = 10**(-6)OPTIONS['learning_rates'] = [1, 0.5, 0.4, 0.3, 0.2, 0.1, 0.01, 0.001, 0.0001]# Datasetimis_3months_dataset = {'name' : 'imis_3months',                        'data_folder' : os.path.join('.', 'data', 'imis_3months'),                        'file_name' : 'imis_3months.txt',                        'labels' : ('lon', 'lat')}imis_3months_dataset_subset1000 = {'name' : 'imis_3months_subset1000',                                   'data_folder' : os.path.join('.', 'data', 'imis_3months'),                                   'file_name' : 'imis_3months_subset1000.txt',                                   'labels' : ('lon', 'lat')}imis_3months_dataset_subset10000 = {'name' : 'imis_3months_subset10000',                                    'data_folder' : os.path.join('.', 'data', 'imis_3months'),                                    'file_name' : 'imis_3months_subset10000.txt',                                    'labels' : ('lon', 'lat')}imis_3months_dataset_subset100000 = {'name' : 'imis_3months_subset100000',                                     'data_folder' : os.path.join('.', 'data', 'imis_3months'),                                     'file_name' : 'imis_3months_subset100000.txt',                                     'labels' : ('lon', 'lat')}imis_3months_dataset_subset1000000 = {'name' : 'imis_3months_subset100000',                                      'data_folder' : os.path.join('.', 'data', 'imis_3months'),                                      'file_name' : 'imis_3months_subset1000000.txt',                                      'labels' : ('lon', 'lat')}OPTIONS['datasets'] = imis_3months_dataset_subset1000000