import osimport mathimport numpy as npimport pandas# Modulesimport parametersdef load_dataset():    """    Load locally stored dataset as specified in parameters.OPTIONS dictionary.        Returns:        initial_dataset:        Randomly select 50% of points        extra_dataset:          Randomly select other 50% of points    """        if 'imis_3months' in parameters.OPTIONS['datasets']['name']:        df = load_imis_3months()            # Select 50% of point as initial_dataset I    n = len(df)    shuffled = df.sample(n)    initial_dataset = shuffled.head(math.floor(n/2))            # Select 50% of points as extra_dataset E    extra_dataset = shuffled.tail(math.floor(n/2))    return initial_dataset, extra_dataset        def load_imis_3months():    """    Load Imis-3months dataset (http://chorochronos.datastories.org/?q=content/imis-3months).            Each record is in the form "t, obj_id, lon, lat" in a large .txt file.    """        # Load Textfile    file_path = os.path.join(parameters.OPTIONS['datasets']['data_folder'],                              parameters.OPTIONS['datasets']['file_name'])        df = pandas.read_table(file_path, delim_whitespace=True, names=('t', 'obj_id','lon', 'lat'))            return df    def export_subset_imis(n):    """    Create a subset of the imis_3months dataset (180+ million points) and     save this locally as 'subsetN.txt'            Args:        n:      Size of subset    """        # Load Full Dataset    file_path = os.path.join(parameters.OPTIONS['datasets']['data_folder'], 'original.txt')    df = pandas.read_table(file_path, delim_whitespace=True, names=('t', 'obj_id','lon', 'lat'))        # Create Subset of size n    subset = df.sample(n)        # Export subset    subset_file_name = os.path.join(parameters.OPTIONS['datasets']['data_folder'], 'subset'+str(n)+'.txt')    np.savetxt(subset_file_name, subset.values, fmt=['%d','%d', '%.13e', '%.13e'])